	public static boolean compareLists(ArrayList<int[]> list1, ArrayList<int[]> list2) {
		// 크기 비교
		if (list1.size() != list2.size()) return false;
		HashSet<Integer> set1 = new HashSet<>();
		HashSet<Integer> set2 = new HashSet<>();
		for (int[] arr : list1) {
			set1.add(Arrays.hashCode(arr));  // 배열을 해싱하여 추가
		}
		for (int[] arr : list2) {
			set2.add(Arrays.hashCode(arr));  // 배열을 해싱하여 추가
		}
		return set1.equals(set2);  // HashSet 비교 (순서 무시)
	}

